const supportedExtensions = ['.jpg', '.jpeg', '.png', '.webp', '.pdf', '.doc', '.docx', '.xls', '.xlsx', '.json', '.xml'];

return $input.all().map(item => {
  // Try to extract file name from binary first, else fall back to message.document
  const fileName = item.binary?.data?.fileName || item.json?.message?.document?.file_name || '';
  const lowerName = fileName.toLowerCase();

  const isSupported = supportedExtensions.some(ext => lowerName.endsWith(ext));

  if (!isSupported) {
    return {
       json: {
        is_supported: false,
        reason: "unsupported_file_type",
        message: '‚ùå The uploaded file type is not supported. Please submit one of the following formats: .jpg, jpeg, .png, .webp, .pdf, .doc, .docx, .xls, .xlsx, .json, .xml',
        fileName: fileName || 'Unknown file'
      }
    };
  }

  // File is supported ‚Äì pass it forward untouched
  return {
    ...item,
    json: {
      ...(item.json || {}),
      is_supported: true,
      reason: "supported_file_type"
    }
  };
});

const results = [];
for (const item of $input.all()) {
const fileName = item.json?.fileName || item.json?.message?.document?.file_name || '';
const ext = fileName.toLowerCase().split('.').pop();

let type = 'unknown';
if (['jpg', 'jpeg', 'png', 'webp'].includes(ext)) {
type = 'image';
} else if (['pdf'].includes(ext)) {
type = 'pdf';
} else if (['doc', 'docx'].includes(ext)) {
type = 'word document';
} else if (['xls', 'xlsx'].includes(ext)) {
type = 'spreadsheet';
} else if (['json'].includes(ext)) {
type = 'json';
} else if (['xml'].includes(ext)) {
type = 'xml file'
}
item.json.fileTypeCategory = type;
results.push(item);
}
return results;
